$alias = Get-Content -Path ([Environment]::GetFolderPath("Desktop")+'\mailboxes.txt')
Get-Mailbox -Identity $alias | Select-Object Displayname,Database,@{Name='TotalItemSize'; Expression={[String]::join(";",((Get-MailboxStatistics -identity $_.identity).TotalItemSize))}},@{Name='ItemCount'; Expression={[String]::join(";",((Get-MailboxStatistics -identity $_.identity).ItemCount))}}, @{Name='IssueWarningQuota'; Expression={[String]::join(";",((Get-MailboxDatabase -identity ($_.identity).DataBase).IssueWarningQuota))}},@{Name='ProhibitSendQuota '; Expression={[String]::join(";",((Get-MailboxDatabase -identity ($_.identity).DataBase).ProhibitSendQuota))}} | export-csv -path ([Environment]::GetFolderPath("Desktop")+'\quota.csv')